DROP TABLE IF EXISTS `gz_abc_calendars`;
CREATE TABLE IF NOT EXISTS `gz_abc_calendars` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL,
  `googleId` varchar(250) DEFAULT NULL,
  `url` varchar(250) DEFAULT NULL,
  `title` varchar(250) DEFAULT NULL,
  `description` text,
  `adults` int(11) NOT NULL,
  `children` int(11) NOT NULL,
  `limit` int(11) NOT NULL,
  `date` TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS `gz_abc_calendars_gallery`;
CREATE TABLE IF NOT EXISTS `gz_abc_calendars_gallery` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `calendar_id` int(11) NOT NULL,
  `name`  varchar(250) DEFAULT NULL,
  `thumb`  varchar(250) DEFAULT NULL,
  `preview`  varchar(250) DEFAULT NULL,
  `original`  varchar(250) DEFAULT NULL,
  `medium`  varchar(250) DEFAULT NULL,
  `date` TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS `gz_abc_users`;
CREATE TABLE IF NOT EXISTS `gz_abc_users` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `email` varchar(250) DEFAULT NULL,
  `first` varchar(250) DEFAULT NULL,
  `last` varchar(250) DEFAULT NULL,
  `avatar` varchar(250) DEFAULT NULL,
  `password` varchar(250) DEFAULT NULL,
  `status` enum('T','F') DEFAULT NULL,
  `type` int(11) NOT NULL,
  `last_login` TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  UNIQUE KEY `email` (`email`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS `gz_abc_price`;
CREATE TABLE IF NOT EXISTS `gz_abc_price` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `calendar_id` int(11) DEFAULT NULL,
  `title` varchar(250) DEFAULT NULL,
  `from_date` varchar(250) DEFAULT NULL,
  `to_date` varchar(250) DEFAULT NULL,
  `monday` float DEFAULT NULL,
  `tuesday` float DEFAULT NULL,
  `wednesday` float DEFAULT NULL,
  `thursday` float DEFAULT NULL,
  `friday` float DEFAULT NULL,
  `saturday` float DEFAULT NULL,
  `sunday` float DEFAULT NULL,
  `is_default` enum('T','F') DEFAULT 'F',
  `adults` int(11) DEFAULT NULL,
  `children`int(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS `gz_abc_discount`;
CREATE TABLE IF NOT EXISTS `gz_abc_discount` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `calendar_id` int(11) DEFAULT NULL,
  `title` varchar(250) DEFAULT NULL,
  `from_date` varchar(250) DEFAULT NULL,
  `to_date` varchar(250) DEFAULT NULL,
  `promo_code` varchar(250) DEFAULT NULL,
  `discount` float DEFAULT NULL,
  `type` enum('price','percent') DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS `gz_abc_extras`;
CREATE TABLE IF NOT EXISTS `gz_abc_extras` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(250) DEFAULT NULL,
  `per` varchar(250) DEFAULT NULL,
  `type` enum('price','percent') DEFAULT NULL,
  `price` float DEFAULT NULL,
  `description` text,
  `img` varchar(250) DEFAULT NULL,
  `status` enum('T','F') DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8;

